<?php//// Script will dump out all nfos in the system into a folder based on the date posted to usenet ./YYYYMMDD/release.nfo// Its not very efficient to pull them all out, should really work out which day you need and go from there.//require_once dirname(__FILE__) . '/../../www/config.php';$db = new \newznab\db\DB();$res = $db->queryDirect("select releases.searchname, releases.postdate, uncompress(releasenfo.nfo) as nfo from releases inner join releasenfo on releases.ID = releasenfo.releaseID and releasenfo.nfo is not null order by postdate");while ($row = $db->getAssocArray($res)){	$dir = date("Ymd", strtotime($row["postdate"]));	if (!file_exists($dir))		mkdir($dir);	$filename = $dir."/".safeFilename($row["searchname"]).".nfo";	if (!file_exists($filename))	{		$fh = fopen($filename, 'w');		fwrite($fh, \newznab\utility\Utility::cp437toUTF($row["nfo"]));		fclose($fh);	}}